import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';
import { Router } from '@angular/router';
import { Docente } from '../docente';
import { flaskLink } from '../link';

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.css']
})
export class RegisterComponent implements OnInit {
  nome: string = '';
  cognome: string= '';
  email: string= '';
  password: string= '';
  errorMessage: string= '';
  successMessage: string= '';
  obsReg: Observable<Docente[]> = undefined!;
  data: any = undefined!;


  constructor(private http: HttpClient, public router: Router) { }

  private baseUrlPrin = flaskLink._API;

  ngOnInit(): void {
  }

  register() {
    this.http.post( this.baseUrlPrin + 'register',{ nome: this.nome, cognome: this.cognome, email: this.email, password: this.password }).subscribe(
      data => {
        console.log(data);
        if (data.hasOwnProperty('error')) {
          this.errorMessage = "Error: Invalid Informations!!";
        } else {
          this.successMessage = "You have successfully Registerd!";
          localStorage.setItem('user', JSON.stringify(data));
          this.router.navigate(['/login']);
        }
      },
      error => {
        console.log(error);
        this.errorMessage = "Error: Invalid Informations!!";
      }
    );
  }
}
<div class="content" role="main">
  <div class="container" style="margin-top: 100px;">
    <h2>User Registration</h2>
    <form (ngSubmit)="register()">
      <div class="form-group mb-2">
        <label for="name">nome:</label>
        <input type="text" class="form-control" id="nome" [(ngModel)]="nome" name="nome" placeholder="nome">
      </div>
      <div class="form-group mb-2">
        <label for="cognome">Cognome</label>
        <input type="text" class="form-control" id="cognome" [(ngModel)]="cognome" cognome="cognome" placeholder="Enter Last Name">
      </div>
      <div class="form-group mb-2">
        <label for="email">Email:</label>
        <input type="email" class="form-control" id="email" [(ngModel)]="email" name="email" placeholder="Enter email">
      </div>
      <div class="form-group mb-4">
        <label for="pwd">Password:</label>
        <input type="password" class="form-control" id="password" [(ngModel)]="password" name="password"
          placeholder="Enter password">
      </div>
      <button type="submit" class="btn btn-primary mb-4">Register</button>

    </form>
  </div>
</div>
